    /// Load a module from data as `Vec<u8>`.
    ///
    /// You can obtains a `Vec<u8>` from a `.ptx` file with the following code:
    /// ```
    /// use std::fs::File;
    /// use std::io::Read;
    ///
    /// let mut file = File::open("resources/kernel.ptx").unwrap();
    ///
    ///  let mut data: Vec<u8> = Vec::new();
    ///  file.read_to_end(&mut data).unwrap();
    ///
    ///  println!("{:?}", data);
    /// ```
    ///
    /// That allow you to embed the `.ptx` file in your binary.
    ///
    /// # Example
    ///
    /// ```
    /// use cuda_driver_sys::*;
    /// use matrix_operations_cuda::cuda_env::CudaEnv;
    /// use matrix_operations_cuda::cuda_module::CudaModule;
    ///
    /// unsafe {
    ///     let cuda_env = CudaEnv::new(0, 0).unwrap();
    ///
    ///     let data = vec![47, 47, 13, 10, 47, 47, 32, 71, 101, 110, 101, 114, 97, 116, 101, 100,
    ///     32, 98, 121, 32, 78, 86, 73, 68, 73, 65, 32, 78, 86, 86, 77, 32, 67, 111, 109, 112, 105,
    ///     108, 101, 114, 13, 10, 47, 47, 13, 10, 47, 47, 32, 67, 111, 109, 112, 105, 108, 101, 114,
    ///     32, 66, 117, 105, 108, 100, 32, 73, 68, 58, 32, 67, 76, 45, 51, 50, 50, 54, 55, 51, 48,
    ///     50, 13, 10, 47, 47, 32, 67, 117, 100, 97, 32, 99, 111, 109, 112, 105, 108, 97, 116, 105,
    ///     111, 110, 32, 116, 111, 111, 108, 115, 44, 32, 114, 101, 108, 101, 97, 115, 101, 32, 49,
    ///     50, 46, 48, 44, 32, 86, 49, 50, 46, 48, 46, 49, 52, 48, 13, 10, 47, 47, 32, 66, 97, 115,
    ///     101, 100, 32, 111, 110, 32, 78, 86, 86, 77, 32, 55, 46, 48, 46, 49, 13, 10, 47, 47, 13,
    ///     10, 13, 10, 46, 118, 101, 114, 115, 105, 111, 110, 32, 56, 46, 48, 13, 10, 46, 116, 97,
    ///     114, 103, 101, 116, 32, 115, 109, 95, 53, 50, 13, 10, 46, 97, 100, 100, 114, 101, 115,
    ///     115, 95, 115, 105, 122, 101, 32, 54, 52, 13, 10, 13, 10, 9, 47, 47, 32, 46, 103, 108, 111,
    ///     98, 108, 9, 116, 101, 115, 116, 13, 10, 13, 10, 46, 118, 105, 115, 105, 98, 108, 101, 32,
    ///     46, 101, 110, 116, 114, 121, 32, 116, 101, 115, 116, 40, 41, 13, 10, 123, 13, 10, 13, 10,
    ///     13, 10, 13, 10, 9, 114, 101, 116, 59, 13, 10, 13, 10, 125, 13, 10, 13, 10];
    ///
    ///     let module = CudaModule::from_vec(data).unwrap();
    ///
    ///     module.free().unwrap();
    ///     cuda_env.free().unwrap();
    /// }
    /// ```
    pub unsafe fn from_vec(module_data: Vec<u8>) -> Result<CudaModule, Box<dyn Error>> {
        let mut module: CUmodule = std::ptr::null_mut();

        let result = cuModuleLoadData(&mut module, module_data.as_ptr() as *const c_void);
        if result != CUresult::CUDA_SUCCESS {
            return Err(format!("Error loading module : {:?}", result).into());
        }

        Ok(CudaModule {
            module,
        })
    }

    /// Load a function from the module
    ///
    /// # Example
    ///
    /// ```
    /// use cuda_driver_sys::*;
    /// use matrix_operations_cuda::cuda_env::CudaEnv;
    /// use matrix_operations_cuda::cuda_module::CudaModule;
    ///
    /// unsafe {
    ///     let cuda_env = CudaEnv::new(0, 0).unwrap();
    ///
    ///     let module = CudaModule::new(b"resources/kernel.ptx\0").unwrap();
    ///     let function = module.load_function(b"add\0").unwrap();
    ///
    ///     module.free().unwrap();
    ///     cuda_env.free().unwrap();
    /// }
    /// ```
    pub unsafe fn load_function(&self, name: &[u8]) -> Result<CUfunction, Box<dyn Error>> {
        let mut function: CUfunction = std::ptr::null_mut();
        let result = cuModuleGetFunction(&mut function, self.module, name.as_ptr() as *const _);
        if result != CUresult::CUDA_SUCCESS {
            return Err(format!("Error loading function: {:?}", result).into());
        }
        Ok(function)
    }

    /// Free the module
    ///
    /// # Example
    ///
    /// ```
    /// use cuda_driver_sys::*;
    /// use matrix_operations_cuda::cuda_env::CudaEnv;
    /// use matrix_operations_cuda::cuda_module::CudaModule;
    ///
    /// unsafe {
    ///     let cuda_env = CudaEnv::new(0, 0);
    ///     let module = CudaModule::new(b"resources/kernel.ptx\0").unwrap();
    ///     module.free().unwrap();
    /// }
    /// ```
    pub unsafe fn free(&self) -> Result<(), Box<dyn Error>> {
        let result = cuModuleUnload(self.module);
        if result != CUresult::CUDA_SUCCESS {
            return Err(format!("Error unloading module : {:?}", result).into());
        }
        Ok(())
    }
